import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

const TextInput = () => {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState('');
  const [ngramsResult, setNgramsResult] = useState('');

  const handleSubmit = async () => {
    try {
      const response = await axios.post(
        'http://localhost:8000/api/submit-text',
        { text: inputText }
      );
      setResult(response.data.result);
    } catch (error) {
      console.error(error);
    }
  };

  const handleNgrams = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/ngrams');
      const ngrams = response.data.ngrams;
      setNgramsResult(ngrams.join(', '));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <TextField
        label="Enter Text"
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />
      <Button variant="contained" onClick={handleSubmit}>
        Submit
      </Button>
      <div>{result}</div>

      <Button variant="contained" onClick={handleNgrams}>
        Get Ngrams
      </Button>
      <div>Ngrams Result: {ngramsResult}</div>
    </div>
  );
};

export default TextInput;
