import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { submitText, getConnectionCount } from './api';
import './styles.css';

const TextInput = () => {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState('');
  const [recentStrings, setRecentStrings] = useState([]);
  const [ngramResults, setNgramResults] = useState([]);
  const [submitted, setSubmitted] = useState(false);
  const handleSubmit = async () => {
    try {
      // Send text to the backend for processing and result
      const response = await axios.post(
        'http://localhost:3000/api/submit-text',
        { text: inputText }
      );

      // Update the state with the result
      setResult(response.data.result);
      setSubmitted(true);
      // Save the submitted text data to the database
      const textDataResponse = await axios.post(
        'http://localhost:3000/api/submit-text-data',
        { text: inputText }
      );

      // Log the connection
      await axios.get('http://localhost:3000/api/log-connection');
      const connectionCount = await getConnectionCount();
      console.log('Number of connections:', connectionCount);
      
    } catch (error) {
      console.error(error);
    }
  };
  const fetchRecentStrings = async () => {
    try {
      const response = await axios.get(
        'http://localhost:3000/api/get-recent-strings'
      );
      setRecentStrings(response.data.recentStrings);
    } catch (error) {
      console.error(error);
    }
  };
  const fetchNgrams = async () => {
    try {
      console.log('Recent Strings:', recentStrings); // Check if recentStrings is populated
      const response = await axios.post(
        'http://localhost:8000/api/process-ngrams',
        {
          strings: recentStrings,
        },
        {
          headers: {
            'Content-Type': 'application/json', // Set the content type to JSON
          },
        }
      );
      console.log(response.data.ngram_results); // Check the received data

      // Set both n-gram similarity results and n-gram data to state
      setNgramResults(response.data.ngram_results);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container">
      <div className="form-section">
        <TextField
          label="Enter Text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        />
        <Button variant="contained" onClick={handleSubmit}>
          Submit
        </Button>
      </div>
      <div className={`output-section ${submitted ? 'show-result' : ''}`}>
        <div className="result">{result}</div>
      </div>
      <div className="action-buttons">
        <Button variant="contained" onClick={fetchRecentStrings}>
          Get Recent 2 Strings
        </Button>
        <Button variant="contained" onClick={fetchNgrams}>
          Process Ngrams
        </Button>
      </div>
      <div className="recent-strings">
        <h2>Recent Strings:</h2>
        <ul>
          {recentStrings.map((recentString, index) => (
            <li key={index}>{recentString}</li>
          ))}
        </ul>
      </div>
      <div className="ngram-results">
        <h2>Ngram Comparison Results:</h2>
        {/* Display n-gram comparison results */}
        <ul>
          {ngramResults.map((ngramResult, index) => (
            <li key={index}>
              <h3>Pair {index + 1}:</h3>
              <ul>
                {ngramResult.similarities.map((similarity, similarityIndex) => (
                  <li key={similarityIndex}>
                    Similarity {similarityIndex + 1}: {similarity.toFixed(2)}
                  </li>
                ))}
              </ul>
              <h4>N-gram Data:</h4>
              <ul>
                {ngramResult.ngram_data.map((ngramData, dataIndex) => (
                  <li key={dataIndex}>
                    <p>
                      <strong>N-gram Set 1:</strong>
                    </p>
                    <ul>
                      {ngramData.ngram_set1.sort().map((ngram, ngramIndex) => (
                        <li key={ngramIndex}>{ngram}</li>
                      ))}
                    </ul>
                    <p>
                      <strong>N-gram Set 2:</strong>
                    </p>
                    <ul>
                      {ngramData.ngram_set2.sort().map((ngram, ngramIndex) => (
                        <li key={ngramIndex}>{ngram}</li>
                      ))}
                    </ul>
                    <p>
                      <strong>Common N-grams:</strong>
                    </p>
                    <ul>
                      {ngramData.common_ngrams
                        .sort()
                        .map((ngram, ngramIndex) => (
                          <li key={ngramIndex}>{ngram}</li>
                        ))}
                    </ul>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TextInput;
