import React, { useState } from 'react';
import {
  submitText,
  submitTextData,
  logConnection,
  getNgramsComparison,
} from './api'; // Import the API functions
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

const TextInput = () => {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState('');
  const [ngramsComparison, setNgramsComparison] = useState('');

  const handleSubmit = async () => {
    try {
      // Send text to the backend for processing and result
      const response = await submitText(inputText);

      // Update the state with the result
      setResult(response);

      // Save the submitted text data to the database
      await submitTextData(inputText);

      // Log the connection
      await logConnection();

      // Get ngrams comparison from Django API
      const ngramsComparisonResponse = await getNgramsComparison();
      setNgramsComparison(ngramsComparisonResponse);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <TextField
        label="Enter Text"
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />
      <Button variant="contained" onClick={handleSubmit}>
        Submit
      </Button>
      <div>{result}</div>
      <div>Ngrams Comparison: {ngramsComparison}</div>
    </div>
  );
};

export default TextInput;
