import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000', // Replace with your backend URL
});

export const submitText = async (text) => {
  try {
    const response = await api.post('/api/submit-text', { text });
    return response.data.result;
  } catch (error) {
    throw error;
  }
};

export const submitTextData = async (text) => {
  try {
    await api.post('/api/submit-text-data', { text });
  } catch (error) {
    throw error;
  }
};

export const logConnection = async () => {
  try {
    await api.get('/api/log-connection');
  } catch (error) {
    throw error;
  }
};

export const getNgramsComparison = async () => {
  try {
    const response = await api.get('/api/process-ngrams');
    return response.data.comparison;
  } catch (error) {
    throw error;
  }
};
